import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FeedbackList = () => {
  const [feedbacks, setFeedbacks] = useState([]);

  const getFeedbacks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/feedbacks/');
      setFeedbacks(response.data);
    } catch (error) {
      console.error('Erro ao buscar feedbacks:', error);
    }
  };

  useEffect(() => {
    getFeedbacks();
  }, []);

  return (
    <div className="max-w-2xl mx-auto p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Feedback List</h2>
      {feedbacks.length > 0 ? (
        <ul className="space-y-4">
          {feedbacks.map((feedback) => (
            <li key={feedback.id} className="bg-white shadow-md rounded-lg p-4">
              <div className="flex justify-between items-center mb-2">
                <span className="text-lg font-semibold text-blue-600">Rating: {feedback.rating}</span>
              </div>
              <p className="text-gray-700 mb-2">
                <span className="font-semibold">Description: </span>{feedback.description}
              </p>
              {/* Add a name or other field if necessary */}
              <p className="text-sm text-gray-500">Feedback ID: {feedback.id}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-gray-500 text-center">No feedback available yet.</p>
      )}
    </div>
  );
};

export default FeedbackList;
