import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
  const [feedbacks, setFeedbacks] = useState([]); // State for feedback list
  const [rating, setRating] = useState(''); // State for feedback rating
  const [description, setDescription] = useState(''); // State for feedback description
  const [message, setMessage] = useState(''); // State for success or error message
  const mentoringId = 1; // Set this to your actual mentoring ID

  // Function to get feedbacks from the API
  const getFeedbacks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/feedbacks/');
      setFeedbacks(response.data); // Now this will work
    } catch (error) {
      console.error('Erro ao buscar feedbacks:', error);
    }
  };

  // Fetch feedbacks when the component mounts
  useEffect(() => {
    getFeedbacks();
  }, []);

  // Submit feedback form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8000/api/feedbacks/', {
        mentoring: mentoringId,
        rating,
        description,
      });
      setMessage('Feedback enviado com sucesso!');
      setRating('');
      setDescription('');
      getFeedbacks(); // Refresh feedback list after submitting
    } catch (error) {
      setMessage('Erro ao enviar feedback.');
    }
  };

  return (
    <div>
      <h1>Enviar Feedback</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Rating:</label>
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            placeholder="DÃª uma nota de 1 a 5"
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Escreva seu feedback"
            required
          />
        </div>
        <button type="submit">Enviar Feedback</button>
      </form>
      {message && <p>{message}</p>} {/* Show feedback message */}
      
      <h2>Feedback List</h2>
      <ul>
        {feedbacks.map(feedback => (
          <li key={feedback.id}>
            <p>Rating: {feedback.rating}</p>
            <p>Description: {feedback.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FeedbackForm;
