import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
    const [rating, setRating] = useState(0);
    const [description, setDescription] = useState('');
    const mentoringId = 1; // Substitua pelo ID real que você deseja usar

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/api/feedback/', {
                mentoring: mentoringId,
                rating,
                description,
            });
            console.log('Feedback enviado com sucesso:', response.data);
        } catch (error) {
            console.error('Erro ao enviar feedback:', error.response);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input 
                type="number" 
                value={rating} 
                onChange={(e) => setRating(e.target.value)} 
                placeholder="Rating (1-5)"
            />
            <textarea 
                value={description} 
                onChange={(e) => setDescription(e.target.value)} 
                placeholder="Descrição do feedback"
            />
            <button type="submit">Enviar Feedback</button>
        </form>
    );
};

export default FeedbackForm;
