import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
    const [mentoringId, setMentoringId] = useState('');
    const [rating, setRating] = useState('');
    const [description, setDescription] = useState('');
    const [message, setMessage] = useState(''); // Estado para mensagem

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/api/feedback/', {
                mentoring: mentoringId,
                rating,
                description
            });

            setMessage('Feedback enviado com sucesso!'); // Mensagem de sucesso
        } catch (error) {
            setMessage('Erro ao enviar feedback.'); // Mensagem de erro
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="ID da Mentoria"
                value={mentoringId}
                onChange={(e) => setMentoringId(e.target.value)}
                required
            />
            <input
                type="number"
                placeholder="Avaliação (1 a 5)"
                value={rating}
                onChange={(e) => setRating(e.target.value)}
                required
            />
            <textarea
                placeholder="Descrição do Feedback"
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                required
            ></textarea>
            <button type="submit">Enviar Feedback</button>
            {message && <p>{message}</p>} {/* Exibe a mensagem ao usuário */}
        </form>
    );
};

export default FeedbackForm;
