import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = ({ mentoringId, setFeedbacks }) => {
    const [rating, setRating] = useState(0);
    const [description, setDescription] = useState('');
    const [name, setName] = useState('');
    const [message, setMessage] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setMessage('');

        if (!name || rating < 1 || rating > 5 || !description) {
            setError('Por favor, preencha todos os campos corretamente.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8000/api/feedbacks/', {
                mentoring_id: mentoringId,
                name,
                rating,
                description,
            });
            setFeedbacks((prevFeedbacks) => [...prevFeedbacks, response.data]);
            setMessage('Feedback enviado com sucesso!');
            setName('');
            setRating(0);
            setDescription('');
        } catch (error) {
            setError('Erro ao enviar feedback. Tente novamente mais tarde.');
            console.error('Error submitting feedback:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit} className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4">
            <h2 className="text-2xl mb-4 font-semibold">Enviar Feedback</h2>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="name">
                    Seu Nome
                </label>
                <input
                    type="text"
                    id="name"
                    placeholder="Seu nome"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring focus:ring-blue-500"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="rating">
                    Avaliação (1-5)
                </label>
                <input
                    type="number"
                    id="rating"
                    placeholder="Rating (1-5)"
                    value={rating}
                    onChange={(e) => setRating(Number(e.target.value))}
                    className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring focus:ring-blue-500"
                    min="1"
                    max="5"
                />
            </div>
            <div className="mb-4">
                <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
                    Descrição
                </label>
                <textarea
                    id="description"
                    placeholder="Descrição"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    className="border border-gray-300 p-2 rounded w-full focus:outline-none focus:ring focus:ring-blue-500"
                    rows="4"
                />
            </div>
            <button
                type="submit"
                className="bg-blue-500 text-white font-semibold py-2 px-4 rounded hover:bg-blue-600 transition"
            >
                Enviar Feedback
            </button>
            {message && <p className="mt-2 text-green-500">{message}</p>}
            {error && <p className="mt-2 text-red-500">{error}</p>}
        </form>
    );
};

export default FeedbackForm;
