import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
  const [mentoringId, setMentoringId] = useState('');
  const [rating, setRating] = useState('');
  const [description, setDescription] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8000/api/feedbacks/', {
        mentoring: mentoringId,
        rating,
        description,
      });
      setMessage('Feedback enviado com sucesso!');

      setRating('');
      setDescription('');
  
      getFeedbacks();
    } catch (error) {
      setMessage('Erro ao enviar feedback.');
    }
  };

  const getFeedbacks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/feedbacks/');
      setFeedbacks(response.data); // Assuming you have feedbacks state
    } catch (error) {
      console.error('Erro ao buscar feedbacks:', error);
    }
  };
  
  

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Mentoring ID" onChange={(e) => setMentoringId(e.target.value)} />
        <input type="number" placeholder="Rating" onChange={(e) => setRating(e.target.value)} />
        <textarea placeholder="Description" onChange={(e) => setDescription(e.target.value)} />
        <button type="submit">Enviar Feedback</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default FeedbackForm;
