import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FeedbackList = () => {
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    // Fetch the feedbacks when the component loads
    const getFeedbacks = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/feedbacks/');
        setFeedbacks(response.data);
      } catch (error) {
        console.error('Erro ao buscar feedbacks:', error);
      }
    };
    
    getFeedbacks();
  }, []);

  return (
    <div className="bg-white p-4 rounded-lg shadow-md mt-6">
      <h2 className="text-xl font-semibold mb-4">Feedback List</h2>
      {feedbacks.length > 0 ? (
        feedbacks.map((feedback) => (
          <div key={feedback.id} className="border-b border-gray-200 pb-4 mb-4">
            <h3 className="text-lg font-medium text-gray-900">Mentor: {feedback.mentoring.mentor_name}</h3>
            <p className="text-sm text-gray-500">Rating: {feedback.rating}</p>
            <p className="text-sm text-gray-700">Description: {feedback.description}</p>
          </div>
        ))
      ) : (
        <p className="text-gray-500">No feedback available.</p>
      )}
    </div>
  );
};

export default FeedbackList;
