import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FeedbackForm = () => {
  const [feedbacks, setFeedbacks] = useState([]); // State for feedback list
  const [rating, setRating] = useState(''); // State for feedback rating
  const [description, setDescription] = useState(''); // State for feedback description
  const [message, setMessage] = useState(''); // State for success or error message
  const mentoringId = 1; // Set this to your actual mentoring ID

  // Function to get feedbacks from the API
  const getFeedbacks = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/feedbacks/');
      setFeedbacks(response.data); // Now this will work
    } catch (error) {
      console.error('Erro ao buscar feedbacks:', error);
    }
  };

  // Fetch feedbacks when the component mounts
  useEffect(() => {
    getFeedbacks();
  }, []);

  // Submit feedback form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8000/api/feedbacks/', {
        mentoring: mentoringId,
        rating,
        description,
      });
      setMessage('Feedback enviado com sucesso!');
      setRating('');
      setDescription('');
      getFeedbacks(); // Refresh feedback list after submitting
    } catch (error) {
      setMessage('Erro ao enviar feedback.');
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 p-6 bg-white shadow-md rounded-lg">
      <h1 className="text-3xl font-bold text-center mb-6">Enviar Feedback</h1>

      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="flex flex-col">
          <label className="font-semibold text-lg">Rating:</label>
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            placeholder="Dê uma nota de 1 a 5"
            required
            className="mt-1 px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>

        <div className="flex flex-col">
          <label className="font-semibold text-lg">Descrição:</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Escreva seu feedback"
            required
            className="mt-1 px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition"
        >
          Enviar Feedback
        </button>
      </form>

      {message && (
        <p
          className={`text-center mt-4 ${
            message.includes('sucesso') ? 'text-green-500' : 'text-red-500'
          }`}
        >
          {message}
        </p>
      )}

      <h2 className="text-2xl font-bold text-center mt-8">Lista de Feedbacks</h2>
      <ul className="mt-4 space-y-4">
        {feedbacks.map((feedback) => (
          <li
            key={feedback.id}
            className="bg-gray-100 p-4 rounded-md shadow-sm"
          >
            <p className="text-lg font-semibold">Rating: {feedback.rating}</p>
            <p className="text-gray-700">Descrição: {feedback.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FeedbackForm;
